import React from "%{ amd: 'react', common: '!React' }";
import ReactModelView from "umd-core/src/views/ReactModelView";
import { _ } from "umd-core/src/common";

module.exports = class Step1View extends ReactModelView {
    get phones() {
        return [
            {
                name: "Nexus S",
                snippet: "Fast just got faster with Nexus S."
            },
            {
                name: "Motorola XOOM™ with Wi-Fi",
                snippet: "The Next, Next Generation tablet."
            },
            {
                name: "MOTOROLA XOOM™",
                snippet: "The Next, Next Generation tablet."
            }
        ];
    }

    render() {
        return (
            <div className="container-fluid">
                <div className="row">
                    <div className="col-md-2">
                        Search:
                        <input spModel="query" />
                    </div>
                    <div className="col-md-10">
                        <ul className="phones">
                            <li spRepeat="(phone, index) in _.filter(this.phones, this.getFilter('query'))" key={ index }>
                                { phone.name }
                                <p>
                                    { phone.snippet }
                                </p>
                            </li>
                        </ul>
                    </div>
                </div>
            </div>
        );
    }
};
